import React, { useState } from "react";

export default function TextForm(props) {
  const handleUpCase = () => {
    let newText = text.toUpperCase();
    setText(newText);
  };
  const handleLowClick = () => {
    let newText = text.toLowerCase();
    setText(newText);
  };
  const handleClearClick = () => {
    let newText = "";
    setText(newText);
  };
  const handleFontPlusClick = (e) => {
    e.target.setText.style.fontSize;
    // setText(newText).style.fontSize;
  };

  const onChangeText = (e) => {
    setText(e.target.value);
  };

  const [text, setText] = useState(" ");
  const wordCount = " " ? text.split(" ").length - 1 : text.split(" ").length;
  return (
    <>
      <div className="container py-5">
        <h3>{props.Heading}</h3>
        <div className="mb-3">
          <textarea
            className="form-control mb-2"
            id="mytextarea"
            rows="10"
            value={text}
            onChange={onChangeText}
          ></textarea>
          <button className="btn btn-primary" onClick={handleUpCase}>
            Convert to Uppercase
          </button>
          <button className="btn btn-success ms-2" onClick={handleLowClick}>
            Convert to Lowercase
          </button>
          <button className="btn btn-success ms-2" onClick={handleClearClick}>
            Clear Text
          </button>
          <button
            className="btn btn-success ms-2"
            onClick={handleFontPlusClick}
          >
            Increase size
          </button>
        </div>
      </div>

      <div className="container my-2">
        <h4>Your Text summary will be here </h4>
        <p>
          <b>{wordCount}</b> words and <b>{text.length}</b> Characters.
          <small>
            {" "}
            And it will take average <b>
              {" "}
              {0.008 * text.split("").length}
            </b>{" "}
            minutes to read
          </small>
        </p>
        <h3 className="mt-4">Preview</h3>
        <p>
          {" "}
          {props.previewtext} {text}
        </p>
      </div>
    </>
  );
}
