import React, { useState } from "react";

export default function TextForm(props) {
  const handelUpClick = () => {
    //console.log(`Uppercase button got clicked  ${text}`);
    let newText = text.toUpperCase();
    setText(newText);
  };
  const handelLowClick = () => {
    //console.log(`Uppercase button got clicked  ${text}`);
    let newText = text.toLowerCase();
    setText(newText);
  };
  const onChangevalue = (e) => {
    //console.log("On Change value");
    setText(e.target.value);
  };

  const [text, setText] = useState("Enter your text here");
  //   setText("jk");
  return (
    <>
      <div className="container py-5">
        <h2>{props.Heading}</h2>
        <div className="mb-3">
          <label for="mytextarea" className="form-label">
            Example textarea
          </label>
          <textarea
            className="form-control mb-2"
            id="mytextarea"
            rows="10"
            value={text}
            onChange={onChangevalue}
          ></textarea>
          <button className="btn btn-primary" onClick={handelUpClick}>
            Convert to Uppercase
          </button>
          <button className="btn btn-success ms-2" onClick={handelLowClick}>
            Convert to Lowercase
          </button>
        </div>
      </div>

      <div className="container my-2">
        <h2>Your Text summary will be here </h2>
        <p>
          <b>{text.split(" ").length}</b> words and <b>{text.length}</b>{" "}
          Characters
        </p>
        <small>
          It will <b> {0.008 * text.split(" ").length}</b> minutes to read
        </small>
        <h3 className="mt-4">Preview</h3>
        <p>{text}</p>
      </div>
    </>
  );
}
