import React, { useState } from "react";

export default function TextForm(props) {
  const onUpCase = () => {
    //setText = text.onUpCase();
    console.log("uppercase button got clicked");
  };
  const onLowCase = () => {
    setText = text.onLowCase();
  };
  const onChangeText = (e) => {
    setText(e.target.text);
  };

  const [text, setText] = useState();

  return (
    <>
      <div class="container py-5">
        <h3>Enter your text to Analyse</h3>
        <div class="mb-3">
          <textarea
            class="form-control mb-2"
            id="mytextarea"
            rows="10"
            value={text}
            onChange={onChangeText}
          ></textarea>
          <button class="btn btn-primary" onClick={onUpCase}>
            Convert to Uppercase
          </button>
          <button class="btn btn-success ms-2" onClick={onLowCase}>
            Convert to Lowercase
          </button>
        </div>
      </div>

      <div class="container my-2">
        <h4>Your Text summary will be here </h4>
        <p>
          <b>20</b> words and <b>30</b> Characters.
          <small>
            {" "}
            And it will take average <b> 0.008</b> minutes to read
          </small>
        </p>
        <h3 class="mt-4">Preview</h3>
        <p></p>
      </div>
    </>
  );
}
