import React, { useState } from "react";

export default function TextForm(props) {
  const handelUpClick = () => {
    //console.log(`Uppercase button got clicked  ${text}`);
    let newText = text.toUpperCase();
    setText(newText);
  };
  const handelLowClick = () => {
    //console.log(`Uppercase button got clicked  ${text}`);
    let newText = text.toLowerCase();
    setText(newText);
  };
  const onChangevalue = (e) => {
    //console.log("On Change value");
    setText(e.target.value);
  };

  const [text, setText] = useState("");
  //   setText("jk");
  return (
    <>
      <div className="container py-5">
        <h3>{props.Heading}</h3>
        <div className="mb-3">
          <textarea
            className="form-control mb-2"
            id="mytextarea"
            rows="10"
            value={text}
            onChange={onChangevalue}
          ></textarea>
          <button className="btn btn-primary" onClick={handelUpClick}>
            Convert to Uppercase
          </button>
          <button className="btn btn-success ms-2" onClick={handelLowClick}>
            Convert to Lowercase
          </button>
        </div>
      </div>

      <div className="container my-2">
        <h4>Your Text summary will be here </h4>
        <p>
          <b>{text.split(" ").length}</b> words and <b>{text.length}</b>{" "}
          Characters.{" "}
          <small>
            And it will take average <b> {0.008 * text.split(" ").length}</b>{" "}
            minutes to read
          </small>
        </p>

        <h3 className="mt-4">Preview</h3>
        <p>{text}</p>
      </div>
    </>
  );
}
