import React, { useState } from "react";

export default function TextForm(props) {
  const handleUpCase = () => {
    let newText = text.toUpperCase();
    setText(newText);
  };
  const handleLowClick = () => {
    let newText = text.toLowerCase();
    setText(newText);
  };

  const onChangeText = (e) => {
    setText(e.target.value);
  };

  const [text, setText] = useState("Please enter your text here");

  return (
    <>
      <div className="container py-5">
        <h3>{props.Heading}</h3>
        <div className="mb-3">
          <textarea
            className="form-control mb-2"
            id="mytextarea"
            rows="10"
            value={text}
            onChange={onChangeText}
          ></textarea>
          <button className="btn btn-primary" onClick={handleUpCase}>
            Convert to Uppercase
          </button>
          <button className="btn btn-success ms-2" onClick={handleLowClick}>
            Convert to Lowercase
          </button>
        </div>
      </div>

      <div className="container my-2">
        <h4>Your Text summary will be here </h4>
        <p>
          <b>12</b> words and <b>{text.length}</b> Characters.
          <small>
            {" "}
            And it will take average <b> 0.008*{text.split("").length}</b>{" "}
            minutes to read
          </small>
        </p>
        <h3 className="mt-4">Preview</h3>
        <p></p>
      </div>
    </>
  );
}
